package Lec1;

public class Multiple_if {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int marks=43;
		if (marks>=75) {
			System.out.println(" A");
		}else if(marks>=65) {
			System.out.println("B");
		}else if(marks>=55) {
			System.out.println("C");
		}else if (marks>=45) {
			System.out.println("pass");
		}else {
			System.out.println("Fail");
		}

	}

}
